function actionAudioFile(e,n,t,o="",i=function(){}){fetch(`/ajax/${e}audio.php?user_id=${n}&audio_id=${t}&new_name=${o}`,{method:"get"}).then(e=>{if(e.status>=200&&e.status<300)return e.text()}).then(n=>{document.querySelector("."+e+"-container").style.display="none",window.displayStatus(n),i()})}function renameFolder(e,n){const t=document.querySelector("#rename-folder-box-"+n+' input[type="text"]').value;actionAudioFile("rename",e,n,t,function(){document.querySelector("#file-"+n+" p").innerHTML=t})}function deleteAudioFile(e,n){actionAudioFile("delete",e,n,"",function(){})}function shareAudioFile(e,n,t){actionAudioFile("share",e,n,"",function(){})}function renameAudioFile(e,n){console.log("Audio Id: "+n);const t=document.querySelector("#"+n+' input[type="text"]').value;console.log("New Name: "+t);const o=n.substring(17);actionAudioFile("rename",e,o,t,function(){document.querySelector("#file-"+o+" p").innerHTML=t})}
