function deleteAudioFile(e,t){fetch(`/ajax/deleteaudio.php?user_id=${e}&audio_id=${t}`,{method:"get"}).then(e=>{if(e.status>=200&&e.status<300)return e.text()}).then(e=>{document.querySelector(".delete-container").style.display="none",window.displayStatus(e)})}function shareAudioFile(e,t,n){fetch(`/ajax/shareaudio.php?user_id=${e}&audio_id=${t}`,{method:"get"}).then(e=>{if(e.status>=200&&e.status<300)return e.text()}).then(e=>{document.querySelector(".share-container").style.display="none",window.displayStatus(e)})}function renameAudioFile(e,t){const n=document.querySelector("#rename-box-"+t+' input[type="text"]').value;fetch(`/ajax/renameaudio.php?user_id=${e}&audio_id=${t}&new_name=${n}`,{method:"get"}).then(e=>{if(e.status>=200&&e.status<300)return e.text()}).then(e=>{window.displayStatus(e),document.querySelector("#rename-box-"+t).style.display="none",document.querySelector("#file-"+t+" p").innerHTML=n})}
